// =============================================================================================================================
// ==================== REF: https://github.com/ganapativs/react-dynamic-import => Advanced usage => 2. HOC ====================
// =============================================================================================================================

import ReactDynamicImport from 'react-dynamic-import';

const languages = ['english', 'spanish'];
const banners = [
    'BSPS_26897',
    'NMMN_11155',
    'NMTG_26903'
];



//#region ==================== components ====================

//  * Define dynamic import loader function
//  * This loads specific module from many available modules using given module name

// const ComponentLoader = f => import(`./components/${f}.js`);
const ComponentLoader = f => import(`../images/ea/banners/imports/${f}.jsx`);


//  * Make sure to use ReactDynamicImport outside the render method, else new component is rendered in each render
//  * You can choose to show a placeholder and render error component in case of error, check API section for more

export const components = languages.reduce((p, c) => {
    return {
        ...p,
        [c]: ReactDynamicImport({
            name: c,
            loader: ComponentLoader
        })
    };
}, {});

//#endregion ==================== components ====================



//#region ==================== HOCComponents ====================

//  * Define dynamic import loader function
//  * This loads specific module from many available modules using given module name

// const HOCLoader = f => import(`./hocs/${f}.js`);
const HOCLoader = f => import(`../images/ea/banners/imports/${f}.jsx`);

// You can import the real component and pass it to HOC

const DummyComponent = ({ text }) => text;


//  * Make sure to use ReactDynamicImport outside the render method, else new component is rendered in each render
//  * You can choose to show a placeholder and render error component in case of error, check API section for more

export const HOCComponents = languages.reduce((p, c) => {

    const DynamicHOC = ReactDynamicImport({
        name: c,
        loader: HOCLoader,
        isHOC: true
    });

    const component = DynamicHOC(DummyComponent);

    return {
        ...p,
        [c]: component
    };
}, {});

//#endregion ==================== HOCComponents ====================
