import React, { lazy, useEffect, useState } from 'react';
import shortid from 'shortid';

const importView = subreddit =>
    lazy(() =>
        import(`../images/ea/banners/imports/${subreddit}View`).catch(() =>
            import(`../images/ea/banners/imports/NullView`)
        )
    );

export default function App({ subredditsToShow }) {

    const bannersToShow = [
        'NMMN_11155',
        'BSPS_26897',
        'pics',
        'NMMN_11155',
        'BSPS_26897',
        'svelte',
        'NMTG_26903',
        'BSPS_26897'
    ];

    const [views, setViews] = useState([]);

    useEffect(() => {
        async function loadViews() {
            const componentPromises =
                subredditsToShow.map(async subreddit => {
                    const View = await importView(subreddit);
                    return <View key={shortid.generate()} />;
                });

            Promise.all(componentPromises).then(setViews);
        }

        loadViews();
    }, [subredditsToShow]);

    return (
        <React.Suspense fallback='Loading views...'>
            <div className='container'>{views}</div>
        </React.Suspense>
    );
}
