// #region ========================= REF: https://www.digitalocean.com/community/tutorials/react-loading-components-dynamically-hooks - switch/case =========================

import React from 'react';
import shortid from 'shortid';

import BSPS_26897 from '../images/ea/banners/imports/BSPS_26897';
import NMMN_11155 from '../images/ea/banners/imports/NMMN_11155';
import NMTG_26903 from '../images/ea/banners/imports/NMTG_26903';
import NullView from '../images/ea/banners/imports/NullView';


export default function Testing123() {

    const bannersToShow = [
        'NMMN_11155',
        'BSPS_26897',
        'pics',
        'NMMN_11155',
        'BSPS_26897',
        'svelte',
        'NMTG_26903',
        'BSPS_26897'
    ];

    const bannerList = bannersToShow.map(
        subreddit => {
            switch (subreddit) {
                case 'NMMN_11155':
                    return <NMMN_11155 key={shortid.generate()} />;
                case 'BSPS_26897':
                    return (
                        <BSPS_26897 key={shortid.generate()} />
                    );
                case 'NMTG_26903':
                    return (
                        <NMTG_26903 key={shortid.generate()} />
                    );
                default:
                    return (
                        <NullView key={shortid.generate()}>
                            {`"r/${subreddit}" - not implemented`}
                        </NullView>
                    );
            }
        }
    );

    return <div>{bannerList}</div>;
}

// #endregion ========================= REF: https://www.digitalocean.com/community/tutorials/react-loading-components-dynamically-hooks - switch/case =========================





